import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const MODEL_MAP = {
  blog: "tiiuae/falcon-7b-instruct",
  code: "tiiuae/falcon-7b-instruct",
  docs: "google/flan-t5-base",
};

const App = () => {
  const [inputText, setInputText] = useState("");
  const [outputText, setOutputText] = useState("");
  const [selectedMode, setSelectedMode] = useState("blog");
  const [loading, setLoading] = useState(false);

    const generatePrompt = (type, userPrompt, outputLength) => {
        let lengthInstruction = "";

        switch (outputLength) {
            case "short":
                lengthInstruction = "Keep the response short and concise.";
                break;
            case "medium":
                lengthInstruction = "Provide a moderate-length response with some details.";
                break;
            case "long":
                lengthInstruction = "Write a detailed and extensive response.";
                break;
            default:
                lengthInstruction = "medium";
        }

        return `${userPrompt}\n\n${lengthInstruction}`;
    };

    const fullPrompt = generatePrompt(selectedType, userPrompt);


    const generateContent = async () => {
    const prompts = {
      blog: `Write a detailed blog post about: ${inputText}`,
      code: `Write code for the following requirement:\n${inputText}`,
      docs: `Write documentation for the following:\n${inputText}`,
    };

    try {
      setLoading(true);
      const model = MODEL_MAP[selectedMode];
      const response = await axios.post(
          `https://api-inference.huggingface.co/models/${model}`,
          { inputs: prompts[selectedMode] },
          {
            headers: {
              Authorization: `Bearer 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`,
            },
          }
      );

      setOutputText(response.data[0]?.generated_text || "No response");
    } catch (err) {
      setOutputText("⚠️ Error: Could not generate response.");
    } finally {
      setLoading(false);
    }
  };

  return (
      <div className="app-container">
        <h1 className="title">📝 AI Writer Pad</h1>

          <div className="mode-selector">
              <label htmlFor="mode">Choose Mode:</label>
              <div>
                  <select value={selectedType} onChange={(e) => setSelectedType(e.target.value)}>
                      <option value="blog">Blog</option>
                      <option value="code">Code</option>
                      <option value="docs">Documentation</option>
                  </select>

                  <textarea
                      placeholder="Write your instruction here..."
                      value={inputText}
                      onChange={(e) => setInputText(e.target.value)}
                  />

                  <div>
                      <label><input type="radio" value="short" checked={selectedLength === "short"}
                                    onChange={(e) => setSelectedLength(e.target.value)}/> Short</label>
                      <label><input type="radio" value="medium" checked={selectedLength === "medium"}
                                    onChange={(e) => setSelectedLength(e.target.value)}/> Medium</label>
                      <label><input type="radio" value="long" checked={selectedLength === "long"}
                                    onChange={(e) => setSelectedLength(e.target.value)}/> Long</label>
                  </div>

                  <button onClick={handleGenerate}>Generate</button>
                  {generateImageFlag && generatedImage && <img src={generatedImage} alt="Generated from AI"/>}
              </div>

              <div className="output-section">
                  <h2>🧠 Output:</h2>
                  <pre>{outputText}</pre>
              </div>
          </div>
      </div>
          );
          };

          export default App;
