import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const MODEL_MAP = {
  blog: "tiiuae/falcon-7b-instruct",
  code: "tiiuae/falcon-7b-instruct",
  docs: "google/flan-t5-base",
};

const App = () => {
  const [inputText, setInputText] = useState("");
  const [outputText, setOutputText] = useState("");
  const [selectedMode, setSelectedMode] = useState("blog");
  const [loading, setLoading] = useState(false);

    const generatePrompt = (type, userPrompt, outputLength) => {
        let lengthInstruction = "";

        switch (outputLength) {
            case "short":
                lengthInstruction = "Keep the response short and concise.";
                break;
            case "medium":
                lengthInstruction = "Provide a moderate-length response with some details.";
                break;
            case "long":
                lengthInstruction = "Write a detailed and extensive response.";
                break;
            default:
                lengthInstruction = "medium";
        }

        return `${userPrompt}\n\n${lengthInstruction}`;
    };

    const fullPrompt = generatePrompt(selectedType, userPrompt, selectedLength);


    const generateContent = async () => {
    const prompts = {
      blog: `Write a detailed blog post about: ${inputText}`,
      code: `Write code for the following requirement:\n${inputText}`,
      docs: `Write documentation for the following:\n${inputText}`,
    };

    try {
      setLoading(true);
      const model = MODEL_MAP[selectedMode];
      const response = await axios.post(
          `https://api-inference.huggingface.co/models/${model}`,
          { inputs: prompts[selectedMode] },
          {
            headers: {
              Authorization: `Bearer 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`,
            },
          }
      );

      setOutputText(response.data[0]?.generated_text || "No response");
    } catch (err) {
      setOutputText("‚ö†Ô∏è Error: Could not generate response.");
    } finally {
      setLoading(false);
    }
  };

  return (
      <div className="app-container">
        <h1 className="title">üìù AI Writer Pad</h1>

        <div className="mode-selector">
          <label htmlFor="mode">Choose Mode:</label>
          <select
              id="mode"
              value={selectedMode}
              onChange={(e) => setSelectedMode(e.target.value)}
          >
            <option value="blog">üìñ Blog</option>
            <option value="code">üíª Code</option>
            <option value="docs">üìÑ Docs</option>
          </select>
        </div>

        <textarea
            rows="6"
            placeholder="Enter your topic or request..."
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
        />

        <button onClick={generateContent} disabled={loading}>
          {loading ? "Generating..." : "üöÄ Generate"}
        </button>

        <div className="output-section">
          <h2>üß† Output:</h2>
          <pre>{outputText}</pre>
        </div>
      </div>
  );
};

export default App;
